// Préparer l'action à exécuter
const query = $input.first().json.query || {};
const action = query.action || query.action_type;
const target = query.target || query.target_user;
const item = query.item || query.role_or_permission;
const decision = query.decision || 'rejected';

if (!action || !target || !item) {
  throw new Error('Paramètres manquants: action, target, item');
}

const isApproved = decision === 'approved';

// Déterminer l'endpoint Gateway selon l'action
let gatewayEndpoint = '';
let requestBody = {};

switch(action) {
  case 'assign_role':
    gatewayEndpoint = '/provision/roles/assign';
    requestBody = {
      user_login: target,
      role_code: item,
      assigned_by: query.requester || 'chatbot-approval',
      source: 'chatbot'
    };
    break;

  case 'remove_role':
    gatewayEndpoint = '/provision/roles/revoke';
    requestBody = {
      user_login: target,
      role_code: item
    };
    break;

  case 'assign_permission':
    gatewayEndpoint = '/provision/permissions/assign';
    requestBody = {
      user_login: target,
      permission_code: item,
      assigned_by: query.requester || 'chatbot-approval'
    };
    break;

  case 'remove_permission':
    gatewayEndpoint = '/provision/permissions/revoke';
    requestBody = {
      user_login: target,
      permission_code: item
    };
    break;

  default:
    throw new Error(`Action non supportée: ${action}`);
}

return {
  action: action,
  target_user: target,
  role_or_permission: item,
  decision: decision,
  is_approved: isApproved,
  gateway_endpoint: gatewayEndpoint,
  request_body: requestBody,
  timestamp: new Date().toISOString()
};
