// Initialiser le workflow multi-étapes
const input = $input.first().json;

const approvalChain = input.approval_chain || [
  { level: 'manager', email: 'manager@example.com', required: true },
  { level: 'dept_head', email: 'dept_head@example.com', required: true },
  { level: 'app_owner', email: 'app_owner@example.com', required: true }
];

const currentStep = input.current_step || 0;
const currentApprover = approvalChain[currentStep];

return {
  ...input,
  approval_chain: approvalChain,
  current_step: currentStep,
  total_steps: approvalChain.length,
  current_approver: currentApprover,
  approval_status: 'pending'
};
